class Circle {
  num x, y, r;
  Circle(this.x, this.y, this.r);
  num get diameter => 2 * r;
  set diameter(num d) {
    this.r = d / 2;
  }
}
void main() {
  Circle c = Circle(0, 0, 3);
  c.diameter = 8;
  print(c.r);
}

